import { useParams } from "react-router-dom"
import axios from "axios";
import { useState, useEffect } from "react";


const DisplayBeerInfo = () => {
  const params = useParams();
  const { id } = params;
  const [beerData, setBeerData] = useState({});
  const base = beerData[0];
  useEffect(() => {
    const fetchData = async () => {
      let url = `https://api.punkapi.com/v2/beers/`
      let res = await axios.get(`${url}${id}`);
      console.log(res.data);
      setBeerData(res.data);
    };
  fetchData()
  },[])

  let hopArr = [];
  base.ingredients.hops.map((hop) => {
    hopArr.push(hop);
    console.log(hopArr);
  })

  return (
    <div>
      <h1>Beer Name</h1>
      <p>{base.name}</p>
      <h1>Description</h1>
      <p>{base.description}</p>
      <h1>Ingredient List</h1>

      <h2>Yeast List</h2>
      <h2>Malt List</h2>
      {base.ingredients.malt.map((malt)=>{
        return <p>{malt.name}</p>
      })}
      <h2>Hops List</h2>
      <h1>Brewing Guidline</h1>
      <h2>Fermentation Temperature</h2>
      <h2>Mash Temperature</h2>
      <h2>Mash Duration</h2>

    </div>
  )
}

export default DisplayBeerInfo
