import Alphabetize from "./Alphabetize";
import {createBrowserHistory} from 'history'

const DisplayHome = ({ datas }) => {
  let history = createBrowserHistory();

  const handleChange = (beer) => {
    history.push(`/description/${beer}`);
    console.log(beer)
}

  return (
    <div>
      <h1>Welcome to Brew Master Index</h1>
      <p>
        Hello my fellow brewer. Are you a first timer and not sure which beer to start with? Or maybe you have explored
        around and are
        not sure what or where to begin. Well you have come to the right place. Explore our vast index of beers and
        ingredients, and
        experience how to get your brew on!
      </p>
      <h2>Check out below to get started</h2>
      <p>Select from the list of beers below or searh by alcohol percentage.</p>
        <select onChange={handleChange}>
          <option>Select a Beer</option>
          <Alphabetize datas={datas} />
        </select>
    </div>
  )
};

export default DisplayHome

