import { useParams } from "react-router-dom"
import axios from "axios";
import { useState, useEffect } from "react";
import RemoveDuplicates from "./RemoveDuplicates";
import FavoritesButton from "./FavoritesButton";

const DisplayBeerInfo = () => {
  const params = useParams();
  const { id } = params;
  const [beerData, setBeerData] = useState({});
  const [favorite, setFavorite] = useState("");
  const base = beerData[0];


  useEffect(() => {
    const fetchData = async () => {
      let url = `https://api.punkapi.com/v2/beers/`
      let res = await axios.get(`${url}${id}`);
      setBeerData(res.data);
      setFavorite(base?.name)
    };
  fetchData()
  },[])

  console.log(base);


  return (
    <div>
      <h1>Beer Name</h1>
      <p>{base?.name}</p>
      <h1>Description</h1>
      <p>{base?.description}</p>
      <h1>Ingredient List</h1>
      <h2>Yeast List</h2>
      <p>{base?.ingredients?.yeast}</p>
      <h2>Malt List</h2>
      {base?.ingredients?.malt.map((malt)=>{
        return <p key={malt?.name}>{malt?.name}</p>
      })}
      <h2>Hops List</h2>
      <RemoveDuplicates base={base}/>
      <h1>Brewing Guidline</h1>
      <h2>Fermentation Temperature</h2>
      <p>{`${base?.method?.fermentation?.temp?.value} degrees ${base?.method?.fermentation?.temp?.unit}`}</p>
      <h2>Mash Temperature</h2>
      <p>{`${base?.method?.mash_temp[0]?.temp?.value} degrees ${base?.method?.mash_temp[0]?.temp?.unit}`}</p>
      <h2>Mash Duration</h2>
      <p>{`${base?.method?.mash_temp[0]?.duration} minutes`}</p>
      <FavoritesButton favorite={favorite}/>
    </div>
  )
}

export default DisplayBeerInfo
